// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./PoolBaseStruct.sol";
import "./../../BaseStructs.sol";

import "./../../../RandomChoice.sol";
import "./../../NFTManager.sol";


abstract contract basePoolVirtual is BaseStructs {

    function mintingPrivate(uint256 tokenRarity, uint256 seasonKey, address to, string memory name) public virtual returns(bool);
    
    function PoolIsEmpty(uint256 seasonID) public virtual returns(bool poolIsEmpty);

    function getRarityForMinting(uint256 r1, uint256 r2) public virtual returns(uint256 rt);

}

abstract contract PoolAvatarVirtual is basePoolVirtual {


    /**
        ABSTRACT FUNCTION TO INERITANCE
    */
    function getAvatarInfo(uint256 avatartID) public view virtual returns(Avatar memory);

    function setAvatarPoolInfo (uint256 seasonID, uint256 rt,  uint256 _quantity, uint256 _cost) public virtual;
    function getAvatarPoolInfo (uint256 seasonID, uint256 rt) public view virtual returns(NFTPoolInfo memory);

    function setAvatarRarity(uint256 r1, uint256 r2, uint256[] memory br) public virtual;
    function getAvatarRarity(uint256 r1, uint256 r2) public view virtual returns (uint256[] memory rr);

}


abstract contract PoolStoneVirtual is basePoolVirtual {

    /**
        ABSTRACT FUNCTION TO INERITANCE
    */
    function getStoneInfo(uint256 StonetID) public virtual view returns(Stone memory);
    
    function setStonePoolInfo (uint256 seasonID, uint256 rt, uint256 _quantity, uint256 _cost ) public virtual;
    function getStonePoolInfo (uint256 seasonID, uint256 rarityColon) public view virtual returns(NFTPoolInfo memory);

    function setStoneRarity(uint256 r1, uint256 r2, uint256[] memory br) public virtual;
    function getStoneRarity(uint256 r1, uint256 r2) public view virtual returns (uint256[] memory rr);
    
}

abstract contract PoolWearableVirtual is basePoolVirtual {

    /**
        ABSTRACT FUNCTION TO INERITANCE
    */
    function getWearableInfo(uint256 WearabletID) public virtual returns(Wearable memory);


    function setWearablePoolInfo (uint256 seasonID, uint256 rt, uint256 _quantity, uint256 _cost) public virtual;
    function getWearablePoolInfo (uint256 seasonID, uint256 rt) public view virtual returns(NFTPoolInfo memory);

    function setWearableRarity(uint256 r1, uint256 r2, uint256[] memory br) public virtual;
    function getWearableRarity(uint256 r1, uint256 r2) public view virtual returns (uint256[] memory rr);   

}


abstract contract PoolBoxVirtual is BaseStructs {

    /**
        ABSTRACT FUNCTION TO INERITANCE
    */
    function createANewBox(uint256 objKey, uint256 r1 ) public virtual returns(uint256 tokenId);
    function openBox(uint256 boxKey) public virtual;

}
